from datetime import datetime
from random import randint




x=randint(1,q-1)#randintは閉区間
y=pow(g,x,p)

now_time=datetime.now()
public_name=now_time.strftime("%Y%m%d%H%M%S_public.txt")
with open(public_name,mode="w") as public_f:
  public_f.write(f"{p}\n{q}\n{g}\n{y}\n")
private_name=now_time.strftime("%Y%m%d%H%M%S_private.txt")
with open(private_name,mode="w") as private_f:
  private_f.write(f"{x}\n{p}\n")#pは復号化に必要なため
